#################

filename = open("out-" + str(int(time.time())) + ".txt","w") 
filename.write("{")

write_to_file(filename, la)

tim = 0

for x in range (1,n+1):
    for j in range (1,n+1-x):
        L_lambda_modifier(j)
        write_to_file(filename, la)

        print(tim)
        # write_to_latex_file("tex" + str(tim).zfill(4) + ".tex", la)

        tim+=1

filename.write("}")
filename.close()

